+<script>
+	document.addEventListener('DOMContentLoaded', function() {
+		// Update quantity
+		const updateQuantity = async (itemId, quantity) => {
+			try {
+				const response = await fetch('/cart/update/', {
+					method: 'POST',
+					headers: {
+						'X-CSRFToken': getCsrfToken(),
+						'Content-Type': 'application/x-www-form-urlencoded',
+					},
+					body: `item_id=${itemId}&quantity=${quantity}`
+				});
+				
+				const data = await response.json();
+				
+				if (data.status === 'success') {
+					// Update display
+					const row = document.querySelector(`tr[data-item-id="${itemId}"]`);
+					row.querySelector('.total-col').textContent = `${data.new_total} ‚Ç¨`;
+					updateCartTotals();
+				} else if (data.status === 'removed') {
+					// Remove row if quantity was 0
+					const row = document.querySelector(`tr[data-item-id="${itemId}"]`);
+					row.remove();
+					updateCartTotals();
+				} else {
+					alert(data.message);
+				}
+			} catch (error) {
+				console.error('Error updating cart:', error);
+				alert('Une erreur est survenue');
+			}
+		};
+	
+		// Handle quantity input changes
+		document.querySelectorAll('.quantity-input').forEach(input => {
+			input.addEventListener('change', function() {
+				const itemId = this.dataset.itemId;
+				const quantity = parseInt(this.value);
+				updateQuantity(itemId, quantity);
+			});
+		});
+	
+		// Handle remove item links
+		document.querySelectorAll('.remove-item').forEach(link => {
+			link.addEventListener('click', function(e) {
+				e.preventDefault();
+				const itemId = this.dataset.itemId;
+				updateQuantity(itemId, 0);
+			});
+		});
+	
+		// Clear cart
+		document.getElementById('clear-cart').addEventListener('click', async function() {
+			if (confirm('√ätes-vous s√ªr de vouloir vider votre panier ?')) {
+				try {
+					const response = await fetch('/cart/clear/', {
+						method: 'POST',
+						headers: {
+							'X-CSRFToken': getCsrfToken()
+						}
+					});
+					
+					const data = await response.json();
+					
+					if (data.status === 'success') {
+						location.reload();
+					} else {
+						alert(data.message);
+					}
+				} catch (error) {
+					console.error('Error clearing cart:', error);
+					alert('Une erreur est survenue');
+				}
+			}
+		});
+	
+		// Helper function to get CSRF token
+		function getCsrfToken() {
+			return document.querySelector('[name=csrfmiddlewaretoken]').value;
+		}
+	
+		// Helper function to update cart totals
+		function updateCartTotals() {
+			const subtotal = Array.from(document.querySelectorAll('.total-col'))
+				.reduce((sum, el) => sum + parseFloat(el.textContent), 0);
+			
+			document.querySelector('.cart-total-card .subtotal span').textContent = 
+				`${subtotal.toFixed(2)} ‚Ç¨`;
+			
+			const shipping = parseFloat(document.querySelector('.cart-total-card .shipping span').textContent);
+			const total = subtotal + (isNaN(shipping) ? 0 : shipping);
+			
+			document.querySelector('.cart-total-card .total span').textContent = 
+				`${total.toFixed(2)} ‚Ç¨`;
+		}
+	});

+</script>
 </html>
