+					headers: {'X-CSRFToken': csrftoken},
+					success: function(response) {
+						if (response.success) {
+							$(`tr[data-item-id="${itemId}"]`).fadeOut(300, function() {
+								$(this).remove();
+								if ($('.cart-table tbody tr').length === 0) {
+									$('.cart-table tbody').append('<tr><td colspan="4" class="text-center">Votre panier est vide</td></tr>');
+								}
+								// Mise √† jour du sous-total
+								location.reload();
+							});
+						}
+					}
+				});
+			}
+	
+			// Gestion du bouton "Clear cart"
+			$('#clear-cart').click(function() {
+				if (confirm("√ätes-vous s√ªr de vouloir vider votre panier ?")) {
