+					},
+					body: `item_id=${itemId}&quantity=${quantity}`
+				})
+				.then(response => response.json())
+				.then(data => {
+					if (data.status === 'success') {
+						// Mettre √† jour le total de l'article
+						const row = document.querySelector(`tr[data-item-id="${itemId}"]`);
+						row.querySelector('.total-col').textContent = `${numberWithCommas(data.item_total)} cfa`;
+						// Mettre √† jour le sous-total
+						document.querySelector('p').textContent = `total : ${numberWithCommas(data.subtotal)} cfa`;
+					} else {
+						alert(data.message);
+					}
+				});
+			});
+		});
+	
+		// Gestionnaire pour la suppression d'un article
+		const removeButtons = document.querySelectorAll('.remove-item');
+		removeButtons.forEach(button => {
+			button.addEventListener('click', function(e) {
+				e.preventDefault();
+				const itemId = this.dataset.itemId;
+				
+				fetch('/remove-cart-item/', {
+					method: 'POST',
+					headers: {
+						'X-CSRFToken': getCookie('csrftoken'),
+						'Content-Type': 'application/x-www-form-urlencoded',
+					},
+					body: `item_id=${itemId}`
+				})
+				.then(response => response.json())
+				.then(data => {
+					if (data.status === 'success') {
+						// Supprimer la ligne du tableau
+						const row = document.querySelector(`tr[data-item-id="${itemId}"]`);
+						row.remove();
+						// Mettre √† jour le sous-total
+						document.querySelector('p').textContent = `total : ${numberWithCommas(data.subtotal)} cfa`;
+					}
+				});
+			});
+		});
+	
+		// Gestionnaire pour vider le panier
+		document.getElementById('clear-cart').addEventListener('click', function() {
+			fetch('/clear-cart/', {
+				method: 'POST',
+				headers: {
+					'X-CSRFToken': getCookie('csrftoken'),
+				}
+			})
+			.then(response => response.json())
+			.then(data => {
+				if (data.status === 'success') {
+					// Vider le tableau
+					const tbody = document.querySelector('tbody');
+					tbody.innerHTML = '<tr><td colspan="4" class="text-center">Votre panier est vide</td></tr>';
+					// Mettre √† jour le sous-total
+					document.querySelector('p').textContent = 'total : 0 cfa';
+				}
+			});
+		});
+	
+		// Fonction utilitaire pour r√©cup√©rer le cookie CSRF
+		function getCookie(name) {
+			let cookieValue = null;
+			if (document.cookie && document.cookie !== '') {
+				const cookies = document.cookie.split(';');
+				for (let i = 0; i < cookies.length; i++) {
+					const cookie = cookies[i].trim();
+					if (cookie.substring(0, name.length + 1) === (name + '=')) {
+						cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
+						break;
+					}
+				}
+			}
+			return cookieValue;
+		}
+	
+		// Fonction utilitaire pour formater les nombres avec des virgules
+		function numberWithCommas(x) {
+			return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
+		}
+	});

