+    <div class="container">
+        <div class="header">
+            <h1>Confirmation de commande</h1>
+            <p>Bonjour {{ user.get_full_name }},</p>
+        </div>
+
+        <div class="order-details">
+            <h2>Détails de votre commande #{{ commande.id }}</h2>
+            <p><strong>Date:</strong> {{ date }}</p>
+            <p><strong>Mode de paiement:</strong> {{ commande.get_payment_method_display }}</p>
+        </div>
+
+        <h3>Articles commandés:</h3>
+        {% for line in order_lines %}
+        <div class="product-item">
+            <p>
+                <strong>{{ line.product.name }}</strong><br>
+                Quantité: {{ line.quantity }}<br>
+                Prix unitaire: {{ line.unit_price }}FCFA<br>
+                Total ligne: {{ line.line_total }}fcfa
+            </p>
+        </div>
+        {% endfor %}
+
+        <div class="total">
+            <p>Total de la commande: {{ total }}FCFA</p>
+        </div>
+
+        <div style="text-align: center; margin-top: 30px; color: #666;">
+            <p>Merci de votre confiance!</p>
+            <p>Si vous avez des questions, n'hésitez pas à nous contacter.</p>
+        </div>
+    </div>
+</body>
+</html>
