{% load static %}
{% load humanize %}

{% load custom_filters %} 
<!DOCTYPE html>
<html lang="fr">

<head>
    <title>Ghislaine Cosmetic</title>
    <meta charset="UTF-8">
    <meta name="description" content="The Plaza eCommerce Template">
    <meta name="keywords" content="plaza, eCommerce, creative, html">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    
    <!-- Favicon -->
    <link href="{% static 'img/logo/ghislaine-icon.png' %}" rel="shortcut icon" />

    <!-- Google Fonts -->
    <link href="https://fonts.googleapis.com/css?family=Raleway:400,400i,500,500i,600,600i,700,700i" rel="stylesheet">
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css">

    <!-- Stylesheets -->
    <link rel="stylesheet" href="{% static 'css/bootstrap.min.css' %}" />
    <link rel="stylesheet" href="{% static 'css/font-awesome.min.css' %}" />
    <link rel="stylesheet" href="{% static 'css/owl.carousel.css' %}" />
    <link rel="stylesheet" href="{% static 'css/style.css' %}" />
    <link rel="stylesheet" href="{% static 'css/animate.css' %}" />

    <!-- Scripts -->
    <script src="{% static 'js/hidden_input.js' %}"></script>
</head>
<style>
	.cart-number {
		display: inline-block; /* Permet de traiter le nombre comme un √©l√©ment de bloc */
		width: 30px; /* Largeur du cercle */
		height: 30px; /* Hauteur du cercle */
		background-color: #ff5733; /* Couleur de fond du cercle */
		color: white; /* Couleur du texte (le nombre) */
		border-radius: 50%; /* Cr√©e le cercle */
		text-align: center; /* Centre le texte horizontalement */
		line-height: 30px; /* Centre le texte verticalement */
		font-weight: bold; /* Style du texte */
		font-size: 1em; /* Taille du texte */
	}
	
	/* Modern Cart Page Styling */

/* Main container styling */
.cart-page.spad {
    padding: 80px 0;
    background-color:#cb2222;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
}

/* Cart table styling */
.cart-table {
    background: #fff;
    border-radius: 10px;
    box-shadow: 0 1px 15px rgba(0, 0, 0, 0.05);
    overflow: hidden;
    margin-bottom: 30px;
}

.cart-table table {
    width: 100%;
    border-collapse: collapse;
}

.cart-table thead {
    background-color:rgb(191, 74, 74);
}

.cart-table th {
    padding: 20px 15px;
    font-weight: 600;
    text-transform: uppercase;
    font-size: 14px;
    color: #333;
    text-align: left;
}

.cart-table td {
    padding: 20px 15px;
    vertical-align: middle;
    border-bottom: 1px solid #eee;
}

/* Product column styling */
.product-col {
    display: flex;
    align-items: center;
}

.product-col img {
    width: 80px;
    height: 80px;
    object-fit: cover;
    border-radius: 5px;
    margin-right: 15px;
}

.pc-title h4 {
    font-size: 16px;
    margin: 0;
    color: #333;
}

/* Price and quantity styling */
.price-col {
    font-weight: 600;
    color: #333;
}

.quy-col .quantity-controls {
    display: flex;
    align-items: center;
    justify-content: flex-start;
}

.quantity-controls .btn {
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color:rgb(193, 50, 50);
    border: none;
    border-radius: 4px;
    font-size: 16px;
    color: #333;
    transition: all 0.3s;
}

.quantity-controls .btn:hover {
    background-color: #e0e0e0;
}

.quantity-controls .quantity {
    margin: 0 10px;
    font-weight: 600;
    min-width: 30px;
    text-align: center;
}

/* Action buttons - Using #cb2222 for the red accent */
.action-col .btn-danger {
    background-color: #cb2222;
    border: none;
    color: white;
    padding: 8px 12px;
    border-radius: 4px;
    transition: all 0.3s;
}

.action-col .btn-danger:hover {
    background-color: #a91c1c; /* Darker shade for hover */
}

/* Footer row */
.cart-table tfoot {
    background-color: #f9f9f9;
}

.cart-table tfoot td {
    padding: 20px 15px;
    text-align: right;
}

/* Empty cart message */
.text-center {
    text-align: center;
    padding: 30px;
    color: #777;
    font-style: italic;
}

/* Cart buttons section */
.cart-buttons {
    margin-top: 30px;
}

.site-btn {
    display: inline-block;
    padding: 12px 30px;
    font-weight: 600;
    border-radius: 5px;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: all 0.3s;
    cursor: pointer;
    text-decoration: none;
}

.btn-continue {
    background-color: #cb2222; /* Using the red accent color */
    color: white;
}

.btn-continue:hover {
    background-color: #a91c1c; /* Darker shade for hover */
}

.btn-clear {
    background-color: #333;
    color: white;
}

.btn-clear:hover {
    background-color: #222;
}

/* Responsive styles */
@media (max-width: 768px) {
    .cart-table thead {
        display: none;
    }
    
    .cart-table tbody tr {
        display: block;
        margin-bottom: 20px;
        border-bottom: 2px solid #eee;
    }
    
    .cart-table tbody td {
        display: block;
        text-align: right;
        padding: 10px 15px;
        border-bottom: 1px solid #f0f0f0;
    }
    
    .cart-table tbody td:before {
        content: attr(data-label);
        float: left;
        font-weight: 600;
        color: #333;
    }
    
    .product-col {
        display: block;
        text-align: center;
    }
    
    .product-col img {
        margin: 0 auto 10px;
    }
    
    .cart-buttons [class*="col-"] {
        text-align: center !important;
        margin-bottom: 15px;
    }
}
	
</style>
 
