@@ -41,9 +41,124 @@
 		font-weight: bold; /* Style du texte */
 		font-size: 1em; /* Taille du texte */
 	}
 
- 
+	/* Page wrapper with a full screen height */
+	.page-area {
+		display: flex;
+		justify-content: center; /* Center horizontally */
+		align-items: center; /* Center vertically */
+		height: 100vh;
+		background-color: #f7f7f7; /* Light background color */
+		padding: 20px;
+		box-sizing: border-box;
+	}
+	
+	/* Container to limit the width */
+	.container {
+		max-width: 900px;
+		width: 100%;
+		background-color: #fff; /* White background for the form */
+		padding: 30px;
+		border-radius: 10px; /* Rounded corners */
+		box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1); /* Soft shadow */
+	}
+	
+	/* Header */
+	.checkout-title {
+		font-size: 24px;
+		font-weight: bold;
+		color: #333;
+		margin-bottom: 20px;
+		text-align: center;
+	}
+	
+	/* Table styling */
+	.order-table {
+		width: 100%;
+		border-collapse: collapse;
+		margin-bottom: 20px;
+	}
+	
+	.order-table th, .order-table td {
+		padding: 12px;
+		text-align: left;
+		font-size: 16px;
+		color: #555;
+	}
+	
+	.order-table th {
+		background-color: #f4f4f4;
+		font-weight: 600;
+	}
+	
+	.order-table td {
+		background-color: #fff;
+	}
+	
+	.order-table tr:nth-child(even) td {
+		background-color: #f9f9f9;
+	}
+	
+	/* Payment method section */
+	.payment-method {
+		display: flex;
+		flex-direction: column;
+		gap: 15px;
+		margin-top: 20px;
+	}
+	
+	.pm-item {
+		display: flex;
+		align-items: center;
+		gap: 10px;
+		font-size: 16px;
+	}
+	
+	input[type="radio"] {
+		accent-color: #6c63ff; /* A soft color for the radio buttons */
+	}
+	
+	/* Submit Button */
+	.site-btn {
+		width: 100%;
+		background-color: #6c63ff; /* A modern color for the button */
+		color: white;
+		padding: 15px;
+		font-size: 18px;
+		border: none;
+		border-radius: 5px;
+		cursor: pointer;
+		transition: background-color 0.3s ease;
+	}
+	
+	.site-btn:hover {
+		background-color: #5a53e6; /* Slightly darker shade on hover */
+	}
+	
+	/* For Mobile Responsiveness */
+	@media (max-width: 768px) {
+		.container {
+			padding: 20px;
+		}
+	
+		.checkout-title {
+			font-size: 20px;
+		}
+	
+		.order-table th, .order-table td {
+			font-size: 14px;
+		}
+	
+		.pm-item {
+			font-size: 14px;
+		}
+	
+		.site-btn {
+			font-size: 16px;
+		}
+	}
+	
 </style>
 <body>
 	 
     <header class="header-section header-normal">

