{% extends 'registration/base.html' %}
{% load static %}
{% load static i18n %}
{% block content %}
<div class="container mt-5">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <div class="card shadow-sm border-0">
                <div class="card-header text-center bg-light border-0">
                    <h2 class="text-muted">Authentification à Deux Facteurs</h2>
                </div>
                <div class="card-body p-4">
                    <form method="post">
                        {% csrf_token %}
                        <div class="form-group mb-4">
                            <h4 class="text-muted">Choisir une méthode de vérification</h4>
                            {% for radio in form.two_factor_method %}
                            <div class="form-check mb-3">
                                {{ radio.tag }}
                                <label class="form-check-label" for="{{ radio.id_for_label }}">
                                    {{ radio.choice_label }}
                                </label>
                            </div>
                            {% endfor %}
                        </div>
                        <button type="submit" class="btn btn-primary btn-lg btn-block rounded-pill">Continuer</button>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>

{% endblock %}
