{% extends 'base.html' %}

{% block content %}
<div class="container">
    <h2>{% if object %}Edit{% else %}Add{% endif %} Product</h2>
    
    <form method="post" enctype="multipart/form-data">
        {% csrf_token %}
        {{ form.as_p }}
        <button type="submit" class="btn btn-primary">Save</button>
        <a href="{% url 'product-list' %}" class="btn btn-secondary">Cancel</a>
    </form>
</div>
{% endblock %}
