@@ -0,0 +1,2293 @@
+/* =================================
+------------------------------------
+  The Plaza - eCommerce Template
+  Version: 1.0
+ ------------------------------------ 
+ ====================================*/
+
+/*----------------------------------------*/
+/* Template default CSS
+/*----------------------------------------*/
+
+html,
+body {
+  height: 100%;
+  font-family: "Raleway", sans-serif;
+  -webkit-font-smoothing: antialiased;
+  font-smooth: antialiased;
+}
+
+h1,
+h2,
+h3,
+h4,
+h5,
+h6 {
+  margin: 0;
+  font-weight: 500;
+  color: #ed0c04;
+}
+
+h1 {
+  font-size: 70px;
+}
+
+h2 {
+  font-size: 36px;
+}
+
+h3 {
+  font-size: 30px;
+}
+
+h4 {
+  font-size: 24px;
+}
+
+h5 {
+  font-size: 18px;
+}
+
+h6 {
+  font-size: 16px;
+}
+
+p {
+  font-size: 14px;
+  color: #f3ebeb;
+  line-height: 2;
+}
+
+img {
+  max-width: 100%;
+}
+
+input:focus,
+select:focus,
+button:focus,
+textarea:focus {
+  outline: none;
+}
+
+a:hover,
+a:focus {
+  text-decoration: none;
+  outline: none;
+}
+
+ul,
+ol {
+  padding: 0;
+  margin: 0;
+}
+
+/*---------------------
+  Helper CSS
+-----------------------*/
+
+.section-title {
+  text-align: center;
+  margin-bottom: 55px;
+}
+
+.section-title h2 {
+  font-size: 30px;
+  text-transform: uppercase;
+  font-weight: 700;
+  color: f67676f8;
+}
+
+.section-title p {
+  font-size: 18px;
+  font-weight: 500;
+  color: #f8b202;
+}
+
+.set-bg {
+  background-repeat: no-repeat;
+  background-size: cover;
+  background-position: bottom center;
+}
+
+.spad {
+  padding-top: 80px;
+  padding-bottom: 80px;
+  background: radial-gradient(
+    circle at center,
+    f67676f8 5%,
+    #f8b202 20%,
+    f67676f8 40%,
+    #f8b202 60%,
+    f67676f8 80%,
+    #f8b202 100%
+  );
+  /* background: -webkit-gradient(
+    linear,
+    left top,
+    right top,
+    from(#c0052eea),
+    color-stop(10%, #c0052eea),
+    to(#f8b202)
+  );
+  background: -o-linear-gradient(left, #c0052eea 50%, #c0052eea 50%, #f8b202 100%);
+  background: linear-gradient(to right, #c0052eea 30%, #c0052eea 30%, #f8b202 100%);
+  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#c0052eea', endColorstr='#f8b202', GradientType=1); */
+}
+
+.text-white h1,
+.text-white h2,
+.text-white h3,
+.text-white h4,
+.text-white h5,
+.text-white h6,
+.text-white p,
+.text-white span,
+.text-white li,
+.text-white a {
+  color: #f8b202;
+}
+
+.rating i {
+  color: #fbb710;
+}
+
+.rating .is-fade {
+  color: #e0e3e4;
+}
+
+/*---------------------
+  Commom elements
+-----------------------*/
+
+/* buttons */
+
+.site-btn {
+  display: inline-block;
+  text-align: center;
+  font-size: 14px;
+  color: #000000;
+  padding: 12px 30px;
+  min-width: 138px;
+  font-weight: 500;
+  border: none;
+  background: #f87102;
+  cursor: pointer;
+  line-height: normal;
+  -webkit-transition: all 0.3s ease 0s;
+  -o-transition: all 0.3s ease 0s;
+  transition: all 0.3s ease 0s;
+}
+
+.site-btn:hover {
+  color: #000000;
+  background: f67676f8;
+}
+
+.site-btn.btn-line {
+  background: transparent;
+  border: 2px solid #f8b202;
+  color: #000000;
+  padding: 12px 20px;
+}
+
+.site-btn.btn-line:hover {
+  background: f67676f8;
+  border: 2px solid f67676f8;
+  color: #414141;
+}
+
+/* Preloder */
+
+#preloder {
+  position: fixed;
+  width: 100%;
+  height: 100%;
+  top: 0;
+  left: 0;
+  z-index: 99;
+  background: linear-gradient(#c0052ecb, #ffd900c4);
+}
+
+.loader {
+  width: 40px;
+  height: 40px;
+  position: absolute;
+  top: 50%;
+  left: 50%;
+  margin-top: -13px;
+  margin-left: -13px;
+  border-radius: 60px;
+  animation: loader 0.6s linear infinite;
+  -webkit-animation: loader 0.6s linear infinite;
+}
+
+@keyframes loader {
+  0% {
+    -webkit-transform: rotate(0deg);
+    transform: rotate(0deg);
+    border: 4px solid f67676f8;
+    border-left-color: transparent;
+  }
+  50% {
+    -webkit-transform: rotate(180deg);
+    transform: rotate(180deg);
+    border: 4px solid #f8b202;
+    border-left-color: transparent;
+  }
+  100% {
+    -webkit-transform: rotate(360deg);
+    transform: rotate(360deg);
+    border: 4px solid f67676f8;
+    border-left-color: transparent;
+  }
+}
+
+@-webkit-keyframes loader {
+  0% {
+    -webkit-transform: rotate(0deg);
+    border: 4px solid f67676f8;
+    border-left-color: transparent;
+  }
+  50% {
+    -webkit-transform: rotate(180deg);
+    border: 4px solid #f8b202;
+    border-left-color: transparent;
+  }
+  100% {
+    -webkit-transform: rotate(360deg);
+    border: 4px solid f67676f8;
+    border-left-color: transparent;
+  }
+}
+
+/*------------------
+  Header section
+---------------------*/
+
+.header-section {
+  position: absolute;
+  width: 100%;
+  padding: 32px 3px 58px;
+  left: 0;
+  top: 0;
+  z-index: 99;
+}
+
+.header-section.header-normal {
+  position: relative;
+  overflow: visible;
+  /* padding: 32px 3px 58px; */
+  padding: 10px 3px 53px;
+  background: f67676f8;
+  background: -webkit-gradient(
+    linear,
+    left top,
+    right top,
+    from(f67676f8),
+    color-stop(10%, f67676f8),
+    to(#f8b202)
+  );
+  background: -o-linear-gradient(left, f67676f8 80%, f67676f8 80%, #f8b202 100%);
+  background: linear-gradient(to right, f67676f8 30%, f67676f8 30%, #f8b202 100%);
+  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='f67676f8', endColorstr='#f8b202', GradientType=1);
+}
+
+.site-logo {
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  float: left;
+}
+
+.site-logo img {
+  border-radius: 50%;
+  width: 60px;
+  height: auto;
+}
+
+.nav-switch {
+  display: none;
+}
+
+.main-menu {
+  list-style: none;
+  margin: 12px auto 0 auto;
+  display: block;
+  float: right;
+  margin-right: 450px;
+}
+
+.main-menu li {
+  display: inline;
+  float: left;
+}
+
+.main-menu li a {
+  display: inline-block;
+  font-size: 14px;
+  text-transform: uppercase;
+  font-weight: 600;
+  color: #fff;
+  padding: 8px 5px 0;
+  margin-left: 50px;
+}
+
+.header-right {
+  float: right;
+  margin: 12px auto 0 auto;
+}
+
+.header-right a {
+  display: inline-block;
+}
+
+.header-right .card-bag {
+  position: relative;
+  text-align: center;
+  margin-right: 15px;
+}
+
+.header-right .card-bag span {
+  position: absolute;
+  width: 23px;
+  height: 23px;
+  left: -7px;
+  bottom: -7px;
+  padding-top: 3px;
+  border-radius: 50px;
+  background: #f8b202;
+  font-size: 12px;
+  color: #fff;
+}
+
+.header-right input {
+  width: 0;
+  height: 33px;
+  border-radius: 2px;
+  border: none;
+  background: #fff;
+  opacity: 0;
+  transform: scaleX(0);
+  transition: opacity 0.3s ease, transform 0.3s ease;
+  pointer-events: none;
+  right: 40px;
+  overflow: hidden;
+  z-index: 10;
+}
+.header-right input.show {
+  width: auto;
+  opacity: 1;
+  transform: scaleX(1);
+  pointer-events: all;
+}
+
+.header-right .search {
+  margin-left: 20px;
+  color: #fff;
+}
+
+.header-right .search i {
+  padding-top: 4px;
+}
+
+/*------------------
+  Hero section
+---------------------*/
+
+.hero-section {
+  height: 100%;
+  background: #000;
+  background-position: bottom center;
+  overflow: hidden;
+}
+
+.hero-slider {
+  height: 100%;
+  overflow: hidden;
+}
+
+.hero-slider .owl-nav {
+  position: absolute;
+  top: calc(50% - 15px);
+  width: 100%;
+  color: #f8b202;
+  font-size: 20px;
+  padding: 0 40px;
+}
+
+.hero-slider .owl-nav .owl-prev {
+  float: left;
+  width: 40px;
+}
+
+.hero-slider .owl-nav .owl-prev:hover,
+.hero-slider .owl-nav .owl-next:hover {
+  color: #ff0202;
+}
+
+.hero-slider .owl-nav .owl-next {
+  float: right;
+  width: 40px;
+  text-align: right;
+}
+
+.hero-slider .owl-dots {
+  position: absolute;
+  right: 46px;
+  bottom: 55px;
+}
+
+.hero-slider .owl-dots .owl-dot {
+  width: 7px;
+  height: 7px;
+  background: #f8b202;
+  display: inline-block;
+  border-radius: 20px;
+  margin-left: 13px;
+}
+
+.hero-slider .owl-dots .owl-dot.active {
+  background: #fff;
+}
+
+.hs-item {
+  height: 100%;
+  overflow: hidden;
+}
+
+.hs-item .hs-left {
+  padding-top: 170px;
+  width: calc(50% + 135px);
+  padding-left: 70px;
+  float: left;
+  opacity: 0;
+}
+
+.hs-item .hs-right {
+  width: calc(50% - 135px);
+  float: left;
+}
+
+.hs-item .hs-content {
+  padding: 260px 0;
+}
+
+.hs-item .hs-content .price {
+  font-size: 18px;
+  font-weight: 700;
+  color: #fff;
+  text-transform: uppercase;
+  position: relative;
+  top: 50px;
+  opacity: 0;
+}
+
+.hs-item .hs-content h2 {
+  font-size: 110px;
+  text-transform: uppercase;
