+  margin-right: 15px;
+}
+
+.header-right .card-bag span {
+  position: absolute;
+  width: 23px;
+  height: 23px;
+  left: -7px;
+  bottom: -7px;
+  padding-top: 3px;
+  border-radius: 50px;
+  background: #f8b202;
+  font-size: 12px;
+  color: #fff;
+}
+
+.header-right input {
+  width: 0;
+  height: 33px;
