+  padding: 10px 15px;
+  border-radius: 5px;
+  text-decoration: none;
+}
+
+.login-btn:hover {
+  background-color: rgb(179, 66, 0); /* Darken the background color on hover */
+  transition: background-color 0.3s ease;
+}

