.checkbox-items .ci-item input[type="checkbox"] {
  visibility: hidden;
  position: absolute;
}

.checkbox-items .ci-item input[type="checkbox"]:checked + label:after {
  background-image: url("../img/icons/check.png");
}

.checkbox-items
  .ci-item
  input[type="checkbox"]:checked
  + label
  + input[type="password"] {
  height: 52px;
  opacity: 1;
  visibility: visible;
  padding: 5px 20px;
  margin-bottom: 15px;
}

