/*
*
* ToTop
* --------------------------------------------------
*/
.ui-to-top {
  position: fixed;
  width: 50px;
  height: 50px;
  font-size: 20px;
  border-radius: 9px;
  right: 15px;
  bottom: 15px;
  overflow: hidden;
  text-align: center;
  text-decoration: none;
  z-index: 120;
  transition: 0.45s all ease-in-out;
  will-change: transform;
  transform: translateY(100px);
}

.ui-to-top::before {
  content: "\f077";
  font-family: "FontAwesome";
  line-height: 50px;
}

.ui-to-top,
.ui-to-top:active,
.ui-to-top:focus {
  color: #ffffff;
  background-color: #f8b202;
}

.ui-to-top:hover {
  color: #ffffff;
  background-color: #ff0037;
  text-decoration: none;
}

.ui-to-top.active {
  transform: translateY(0);
}

html.mobile .ui-to-top,
html.tablet .ui-to-top {
  display: none !important;
}

@media (min-width: 576px) {
  .ui-to-top {
    right: 40px;
    bottom: 40px;
  }
}



